UPDATE db_partitions_ingestion
SET status = 'KO'
WHERE ff_uuid = '${partition.id}';

UPDATE db_partitions_ingestion
SET
	status = '${status.code}',
	storage_date = ${wn_ingestion.timestamp:isNull():ifElse("null", ${wn_ingestion.timestamp:format("yyyy-MM-dd HH:mm:ss.SSS"):prepend("TIMESTAMP '"):append("'")})},
	storage_name = ${storage_name:isNull():ifElse("null",${storage_name:prepend("'"):append("'")})}
WHERE ff_uuid = '${partition.id}';

UPDATE db_tables_processing
SET status = 'KO'
WHERE ff_uuid = '${source.uuid}';

UPDATE db_tables_processing
SET
	status = '${status.code:isNull():ifElse(${wait.counter.KO:notNull():ifElse('KO', 'OK')}, ${status.code})}',
	storage_date = TIMESTAMP '${now():toNumber():format("yyyy-MM-dd HH:mm:ss.SSS")}',
	number_partitions = ${fragment.count}
WHERE ff_uuid = '${source.uuid}';
